<script>export default { data() { return { teams: [{ name: '队伍A', leaderName: '队长A', leaderAvatar: 'https://via.placeholder.com/150', description: '这是2023年校内编程比赛的队伍A', type: '校内赛', currentMembers: 10, totalMembers: 20, status: '招募中', tags: ['标签1', '标签2', '标签3'] }, { name: '队伍B', leaderName: '队长B', leaderAvatar: 'https://via.placeholder.com/150', description: '这是2023年市级编程比赛的队伍B', type: '市赛', currentMembers: 15, totalMembers: 25, status: '招募完成', tags: ['标签4', '标签5', '标签6'] }, { name: '队伍C', leaderName: '队长C', leaderAvatar: 'https://via.placeholder.com/150', description: '这是2022年省级编程比赛的队伍C', type: '省赛', currentMembers: 12, totalMembers: 18, status: '招募中', tags: ['标签7', '标签8', '标签9'] }, { name: '队伍D', leaderName: '队长D', leaderAvatar: 'https://via.placeholder.com/150', description: '这是2021年全国编程比赛的队伍D', type: '国赛', currentMembers: 20, totalMembers: 25, status: '招募完成', tags: ['标签10', '标签11', '标签12'] }], searchText: '', selectedStatus: '', selectedTags: [], selectedType: '', filteredTeams: [], searchResults: [] } }, computed: { allTags() { const allTags = new Set(); this.teams.forEach(team => { team.tags.forEach(tag => allTags.add(tag)); }); return Array.from(allTags); }, allTypes() { const allTypes = new Set(); this.teams.forEach(team => { allTypes.add(team.type); }); return Array.from(allTypes); } }, created() { this.filteredTeams = this.teams; }, methods: { filterTeams() { this.filteredTeams = this.teams.filter(team => { // 根据搜索框内容、状态、标签、比赛类型筛选条件 const searchText = this.searchText.toLowerCase(); const matchesSearchText = team.name.toLowerCase().includes(searchText) || team.leaderName.toLowerCase().includes(searchText) || team.tags.some(tag => tag.toLowerCase().includes(searchText)); const matchesStatus = this.selectedStatus ? team.status === this.selectedStatus : true; const matchesTags = this.selectedTags.length ? this.selectedTags.every(tag => team.tags.includes(tag)) : true; const matchesType = this.selectedType ? team.type === this.selectedType : true; return matchesSearchText && matchesStatus && matchesTags && matchesType; }); }, // ... 其他方法保持不变 } } </script>